// Pixel fix to open all popups in correct offset
function setProductOffset(obj){
    var objOffset = new Object();
    var offL = -5;
    var offT = -10;
    if($('compare') != null) {
        offL = -4;
        offT = -9;
    }
    var elem = obj.parentNode.childNodes.length;
    var prevCols = obj.previousSiblings().length;
    var myPos = prevCols + 1;
    
    if (navigator.platform.toLowerCase().indexOf('mac') != -1) {
        if(Prototype.Browser.Gecko){
            if(myPos == 5){offL = -4;}
        }
        else if(Prototype.Browser.WebKit){
            offL = -4;
            offT = -9;
            if(myPos == 1){offL = -5;}
        }
    }
    else{
        if(Prototype.Browser.IE){            
            if($('compare') == null) {
                offL = -4;
                offT = -10;
                if(myPos == 1){offL = -5;}
            } else {
                offL = -4;
                offT = -9;
                if(elem==3){
                    if(myPos == 1){offL = -4;}
                    else if(myPos % 2 == 0){offL = -2;}
                    else if(myPos % 2 != 0){offL = -3;}
                }
            }
        }
        else if(Prototype.Browser.Gecko){
            if($('compare') == null) {
                if(myPos == 5){offL = -4;}
            } else {
                if(myPos % 2 == 0){offL = -3;}
            }
        }
        else if(Prototype.Browser.WebKit){
            offL = -4;
            offT = -9;
            if(myPos == 1){offL = -5;}
        }
    }
    objOffset.left = offL;
    objOffset.top = offT;
    return objOffset;
}
